def Binary_Representation_Without_MSB(x):
    binary = "{0:b}".format(int(x))
    binary_without_MSB = binary[1:]
    return binary_without_MSB
 
def EliasGammaEncode(k):
    if (k == 0):
        return '0'
    N = 1 + floor(log(k, 2))
    Unary = (N-1)*'0'+'1'
    return Unary + Binary_Representation_Without_MSB(k)
 
def EliasDeltaEncode(k):
    Gamma = EliasGammaEncode(1 + floor(log(k, 2)))
    binary_without_MSB = Binary_Representation_Without_MSB(k)
    return Gamma+binary_without_MSB

'''Тесты на корректность гамма-кодирования Элиаса'''
def test_1_1():
    assert EliasGammaEncode(1) == '1'

def test_1_2():
    assert EliasGammaEncode(128) == '000000010000000'

def test_1_3():
    assert EliasGammaEncode(129) == '000000010000001'

def test_1_4():
    assert EliasGammaEncode(65536) == '000000000000000010000000000000000'

def test_1_5():
    assert EliasGammaEncode(5) == '00101'

